import React, { useState, useMemo } from 'react'
// import TinderCard from '../react-tinder-card/index'
import TinderCard from 'react-tinder-card'
import '../styles/App.css';
const db = [
  {
    name: 'White Pipe',
    url: './img/pipe.jpg'
  },
  {
    name: 'Black Pipe',
    url: './img/blackpipe.jpg'
  },
  {
    name: 'Coil',
    url: './img/coil.jpg'
  },
  {
    name: 'Adapter',
    url: './img/adapter.jpg'
  },
  {
    name: 'Elbow',
    url: './img/elbow.jpg'
  }
]

const alreadyRemoved = []
let charactersState = db // This fixes issues with updating characters state forcing it to use the current state and not the state that was active when the card was created.

function Tindercard () {
  const [characters, setCharacters] = useState(db)
  const [lastDirection, setLastDirection] = useState()
  const [countYes, setCounterYes] = useState(0);
  const [countNo, setCounterNo] = useState(0);
  var audiolike = new Audio("./sounds/Like.wav");
  var audiodislike = new Audio("./sounds/Dislike.wav");
  const childRefs = useMemo(() => Array(db.length).fill(0).map(i => React.createRef()), [])

  const swiped = (direction, nameToDelete) => {
    const cardsLeft = characters.filter(person => !alreadyRemoved.includes(person.name))
    if (cardsLeft.length) {
    console.log('removing: ' + nameToDelete)
    configureswipe(direction,nameToDelete);
    }
  }

  const outOfFrame = (name) => {
    console.log(name + ' left the screen!')
    charactersState = charactersState.filter(character => character.name !== name)
    setCharacters(charactersState)
  }


  const swipe = (dir) => {

    const cardsLeft = characters.filter(person => !alreadyRemoved.includes(person.name))
    if (cardsLeft.length) {

      const toBeRemoved = cardsLeft[cardsLeft.length - 1].name // Find the card object to be removed
      const index = db.map(person => person.name).indexOf(toBeRemoved) // Find the index of which to make the reference to
      configureswipe(dir,toBeRemoved);
      childRefs[index].current.swipe(dir) // Swipe the card!
    }
  }

  function configureswipe(dir,toBeRemoved)
  {
    if(direction==="right"){audiolike.play(); setCounterYes(countYes + 1);}else{audiodislike.play();setCounterNo(countNo + 1);}
    setLastDirection(dir);
    alreadyRemoved.push(toBeRemoved) // Make sure the next card gets removed next time if this card do not have time to exit the screen

  }
  return (
    <div>

      <h1>Getter Products</h1>
      <div className='cardContainer'>
        {characters.map((character, index) =>
          <TinderCard ref={childRefs[index]} className='swipe' key={character.name} onSwipe={(dir) => swiped(dir, character.name)} onCardLeftScreen={() => outOfFrame(character.name)}>
            <div style={{ backgroundImage: 'url(' + character.url + ')' }} className='card'>
              <h3>{character.name}</h3>
            </div>
          </TinderCard>
        )}
      </div>
      <div className='buttons'>
        <button onClick={() => swipe('left')}>No! {countNo}</button>
        <button onClick={() => swipe('right')}>Yes! {countYes}</button>
      </div>
      {lastDirection ? <h2 key={lastDirection} className='infoText'>You swiped {lastDirection}</h2> : <h2 className='infoText'>Swipe a card or press a button to get started!</h2>}
    </div>
  )
}

export default Tindercard;
